import { Component } from '@angular/core';
// importamos los matedos para el formulario
import {FormGroup,FormBuilder} from '@angular/forms';
// importamos los servicio
import { ToysService } from 'src/app/servicio/toys/toys.service';
@Component({
  selector: 'app-toys-form',
  templateUrl: './toys-form.component.html',
  styleUrls: ['./toys-form.component.css']
})
export class ToysFormComponent {

  //creamos el objecto del formulario reactivo
  FormularioToys:FormGroup;



  //
  constructor(
    public formulario:FormBuilder,
    private toysService:ToysService
  ){

    this.FormularioToys = this.formulario.group({
      // traemos  a cada uno de los inputos 
      name:[''],
      toys:[''],
      gender:[''],
      fn:['']
    });
  }

  enviarDatos():any{
    console.log(this.FormularioToys.value);
    // enviamos el formulario al metodo el .subscribe espara aplice los cambios
    this.toysService.AgregarToys(this.FormularioToys.value).subscribe()
  }
}
<div class="card">
    <h5 class="card-header">Registra donacion de juguetes</h5>
    <div class="card-body">
        <form [formGroup]="FormularioToys" (ngSubmit)="enviarDatos()">
            <div>
                <div class="mb-3">
                    <label for="exampleFormControlInput1" class="form-label">Nombre del donador</label>
                    <input type="text" class="form-control" id="" formControlName="name" placeholder="">
                </div>
                <div class="mb-3">
                    <label for="exampleFormControlInput1" class="form-label">Juguete</label>
                    <input type="text" class="form-control" id="" formControlName="toys" placeholder="">
                </div>
                <div class="mb-3">
                    <label for="exampleFormControlInput1" class="form-label">Genero</label>
                    <select formControlName="gender" class="form-select">
                        <option value="hombre">Hombre</option>
                        <option value="mujer">Mujer</option>
                    </select>
                </div>
                <div class="mb-3">
                    <label for="exampleFormControlInput1" class="form-label">Fecha de donado</label>
                    <input type="date" class="form-control" id="" formControlName="fn" placeholder="">
                </div>
            </div>
            <div class="btn-group" role="group" aria-label="Basic mixed styles example">
                <button type="submit" class="btn btn-success">Guardar</button>
                <button type="button" class="btn btn-warning">Eliminar</button>
              </div>
        </form>
    </div>
</div>
