import { Component } from '@angular/core';

import { MoneyService } from 'src/app/servicio/money/money.service';

import {FormGroup,FormBuilder} from '@angular/forms';

@Component({
  selector: 'app-money-form',
  templateUrl: './money-form.component.html',
  styleUrls: ['./money-form.component.css']
})
export class MoneyFormComponent {
  FormularioMoney:FormGroup;


  //
  constructor(
    public formulario:FormBuilder,
    private moneyService:MoneyService
  ){

    this.FormularioMoney = this.formulario.group({
      name:[''],
      money:[''],
      fn:['']
    });
  }

  enviarDatos():any{
    console.log(this.FormularioMoney.value);
    this.moneyService.AgregarMoney(this.FormularioMoney.value).subscribe()
  }
}
<div class="card">
    <h5 class="card-header">Registra donacion money</h5>
    <div class="card-body">
        <form [formGroup]="FormularioMoney" (ngSubmit)="enviarDatos()">
            <div>
                <div class="mb-3">
                    <label for="exampleFormControlInput1" class="form-label">Nombre</label>
                    <input type="tetx" class="form-control" id="" formControlName="name" placeholder="">
                </div>
                <div class="mb-3">
                    <label for="exampleFormControlInput1" class="form-label">Money</label>
                    <input type="number" class="form-control" id="" formControlName="money" placeholder="">
                </div>
                <div class="mb-3">
                    <label for="exampleFormControlInput1" class="form-label">Fecha</label>
                    <input type="date" class="form-control" id="" formControlName="fn" placeholder="">
                </div>
            </div>
            <div class="btn-group" role="group" aria-label="Basic mixed styles example">
                <button type="submit" class="btn btn-success">Guardar</button>
                <button type="button" class="btn btn-warning">Eliminar</button>
              </div>
        </form>
    </div>
</div>
